mapper.select:
select
  account_id, account_account, account_password, account_picture, account_type, account_nickname, person_id
from account


        and account_id = #{accountId}


        and account_account = #{accountAccount}


        and account_password = #{accountPassword}


        and account_picture = #{accountPicture}


        and account_type = #{accountType}


        and account_nickname = #{accountNickname}


        and person_id = #{personId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from account


        and account_id = #{accountId}


        and account_account = #{accountAccount}


        and account_password = #{accountPassword}


        and account_picture = #{accountPicture}


        and account_type = #{accountType}


        and account_nickname = #{accountNickname}


        and person_id = #{personId}

select *
from account


        and account_account = #{accountAccount}


        and account_password = #{accountPassword}
